(6, 1, '2026-02-28 14:27:35', '2026-02-28 14:27:35', '<!-- wp:image {\"id\":7,\"width\":\"662px\",\"height\":\"auto\",\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"align\":\"center\"} -->\n<figure class=\"wp-block-image aligncenter size-large is-resized\"><img src=\"http://localhost/Rently/wp-content/uploads/2026/02/bedroom_1-1024x768.avif\" alt=\"\" class=\"wp-image-7\" style=\"width:662px;height:auto\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">About this space</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>A spacious and luxurious house for short and individual stay.<br><br>The space<br>Private room with kitchen and bathroom toilet<br><br>Guest access<br>Private room<br><br>Other things to note<br>Need to pick up the key</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">What this place offers</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Bathroom</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Hair dryer</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Cleaning products</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Shampoo</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Conditioner</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Body soap</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Hot water</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Shower gel</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Bedroom and laundry</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Free washer</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>EssentialsTowels, bed sheets, soap, and toilet paper</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Hangers</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Bed linensCotton linens</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Extra pillows and blankets</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Room-darkening shades</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Iron</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Drying rack for clothing</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Safe</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Mosquito net</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Clothing storage: closet</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Entertainment</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Ethernet connection</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>HDTV</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Bluetooth sound system</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Books and reading material</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Heating and cooling</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Central air conditioning</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Ceiling fan</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Central heating</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Home safety</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Exterior security cameras on propertyWe have security cameras in the house</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Smoke alarm</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Carbon monoxide alarm</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>First aid kit</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Internet and office</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Wifi</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Dedicated workspace</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Kitchen and dining</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>KitchenSpace where guests can cook their own meals</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Refrigerator</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Microwave</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Cooking basicsPots and pans, oil, salt and pepper</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Dishes and silverwareBowls, chopsticks, plates, cups, etc.</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Mini fridge</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Hot water kettle</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Wine glasses</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Toaster</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Rice maker</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Dining table</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Location features</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Ski-in/Ski-outGuests can access ski lifts without driving or taking paid transportation</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Private entranceSeparate street or building entrance</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Laundromat nearby</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Resort accessGuests can use nearby resort facilities</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Outdoor</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Private patio or balcony</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Parking and facilities</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Free parking garage on premises</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Free street parking</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>ElevatorThe home or building has an elevator thats at least 52 inches deep and a doorway at least 32 inches wide</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Paid parking garage off premises</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Paid parking garage on premises</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Services</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Long term stays allowedAllow stay for 28 days or more</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Cleaning available during stay</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Not included</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Unavailable: Dryer</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'Service Apartment Farmgate Monipuripara', '', 'publish', 'closed', 'closed', '', 'service-apartment-farmgate-monipuripara', '', '', '2026-02-28 14:28:24', '2026-02-28 14:28:24', '', 0, 'http://localhost/Rently/?post_type=property&#038;p=6', 0, 'property', '', 0),
