(14, 1, '2026-02-26 18:18:41', '2026-02-26 18:18:41', '<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Entire serviced apartment in Dhaka, Bangladesh</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol class=\"wp-block-list\"><!-- wp:list-item -->\n<li>2 guests</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>1 bedroom</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>1 bed</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>1 bath</li>\n<!-- /wp:list-item --></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p> IMPORTANT: The Windows are Dangerous. Please take extra care, especially with children.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Located in the HEART of Dhaka.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Building address is &nbsp;Kazi Nazrul Islam Avenue, Kawran Bazar, Dhaka, Bangladesh&nbsp;<small>(Current Location)</small></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Unit is on 15 th Floor</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>No Deposit needed</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Free Parking</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Free WIFI</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Free Infinity Pool and Gym</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>The Cleanest space in the world building</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Other things to note<br>No TV in this Space. Please check our other listings if you need a TV during your stay.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'Ceylonz Suites 33A (A) high floor City View', '', 'publish', 'closed', 'closed', '', 'ceylonz-suites-33a-a-high-floor-city-view', '', '', '2026-02-26 19:33:32', '2026-02-26 19:33:32', '', 0, 'http://localhost/Rently/?post_type=rently_property&#038;p=14', 0, 'rently_property', '', 0),
